
var sum = 5 + 3; // sum = 8
console.log("Sum:", sum);
// Subtraction
var difference = 10 - 4; // difference = 6
console.log("Difference:", difference);
// Multiplication
var product = 2 * 6; // product = 12
console.log("Product:", product);
// Division
var quotient = 20 / 5; // quotient = 4
console.log("Quotient:", quotient);
// Modulus (Remainder)
var remainder = 10 % 3; // remainder = 1 (10 divided by 3 equals 3 with a remainder of 1)
console.log("Remainder:", remainder);
// Increment
var x = 5;
x++; // Increment x by 1, now x = 6
console.log("Incremented x:", x);
// Decrement
var y = 8;
y--; // Decrement y by 1, now y = 7
console.log("Decremented y:", y);
